mapper.select:
SELECT * FROM workcase WHERE 1=1
AND homepageShow = 1
ORDER BY createTime ASC;

SELECT * FROM workcase WHERE 1=1
AND id = #{id}
ORDER BY createTime ASC;

SELECT * FROM workcasepicture WHERE 1=1

    AND workcaseId = #{workcaseId}

AND iscover = 0

SELECT * FROM workcasepicture WHERE 1=1

    AND workcaseId = #{workcaseId}

AND iscover = 1

SELECT * FROM singlepicture WHERE 1=1
AND `type` = #{type}
ORDER BY createTime ASC;

SELECT * FROM industryinformation WHERE 1=1
AND id = #{id}
ORDER BY updateTime DESC;

SELECT * FROM industryinformationpicture WHERE 1=1
AND industryinformationId = #{industryinformationId}
ORDER BY updateTime ASC;
